import React, {useState} from 'react'
import {Link} from 'react-router-dom'
import './ProjectLayout.css'



function ProjectLayout(props) {
  return (
  <div className='project-layout'>
    <Link to='/projects' className='back'>
        back to projects
    </Link>
    <div className="layout-title">{props.title}</div>
    <div class="slideshow-container">
      {slide === 1 && <img className="layout-img" src={props.img1} />}
      {slide === 2 && <img className="layout-img" src={props.img2} />}
      {slide === 3 && <img className="layout-img" src={props.img3} />}
      <div className="slide-buttons">
        <a className="prev" onClick="plusSlides(-1)">&#10094;</a>
        <a className="next" onClick="plusSlides(1)">&#10095;</a>
      </div>
    </div>
    <div className="proj-info layout-link">{props.link}</div>
    <div className="proj-info layout-desc">{props.desc}</div>
  </div>
  );
}

export default ProjectLayout
